export function registerFonts() {
  for (const [fontName, data] of Object.entries(fontDefs)) {
    CONFIG.fontDefinitions[fontName] = {
      editor: data.editor ?? false,
      fonts: data.fonts.map((f) => ({ urls: f.urls })),
    };
  }
  console.log("Pars Crucis | Fonts registered:", Object.keys(fontDefs));
}
